export type LanguageCode = typeof languages[number]['code'];

export const defaultLang = 'en';

export type Dictionary = typeof translations[typeof defaultLang];
export type DictionaryKey = keyof Dictionary;

export const getDictionary = (lang: LanguageCode): Dictionary => {
    return translations[lang] || translations[defaultLang];
}

// A helper function to translate dates or other dynamic content
export function translateDate(key: DictionaryKey, lang: LanguageCode, replacements: Record<string, string>): string {
    const translation = getDictionary(lang)[key] || key;
    if (typeof translation !== 'string') return key;

    return Object.entries(replacements).reduce((acc, [key, value]) => {
        return acc.replace(`{${key}}`, value);
    }, translation);
}
    
